import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  AiFillDelete,
  AiOutlineEdit,
  AiOutlineCloseSquare,
} from "react-icons/ai";
import "./App.css";

const App = () => {
  const baseUrl = "https://reqres.in/api/users";
  const [userList, setUserList] = useState([]);
  const [email, setEmail] = useState("");
  const [first_name, setFirst_Name] = useState("");
  const [last_name, setLast_Name] = useState("");
  const [avatar, setAvatar] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    axios.get(`${baseUrl}`).then((response) => {
      setUserList(response.data.data);
    });
  }, [baseUrl]);

  // console.log(userList);

  const createUser = async (e) => {
    e.preventDefault();
    if (!email || !first_name || !last_name || !avatar) {
      alert("Need to enter enough information");
      return;
    }
    const postData = {
      id: userList.length + 1,
      email,
      first_name,
      last_name,
      avatar,
    };
    await axios.post(baseUrl, postData).then((result) => {
      setUserList([...userList, result.data]);
      // console.log(userList);
      // console.log(result.data.id);
    });
  };

  const deleteData = async (id) => {
    try {
      await axios.delete(baseUrl, `/${id}`);
      const userChange = userList.filter((user) => user.id !== id);
      setUserList(userChange);
      console.log(userChange);
    } catch (err) {
      console.log(`Error: ${err.message}`);
    }
  };

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="App">
      <div className="userList">
        {userList?.map((user) => {
          const { id, email, first_name, last_name, avatar } = user;
          return (
            <div className="user" key={id}>
              <div>
                <img src={avatar} alt="" />
              </div>
              <div className="info">
                <h1>
                  {first_name} {last_name}
                </h1>
                <p>{email}</p>
              </div>
              <div className="icons">
                <button className="btn" onClick={() => deleteData(id)}>
                  <AiFillDelete />
                </button>
                <button className="btn" onClick={togglePopup}>
                  <AiOutlineEdit />
                </button>
              </div>
            </div>
          );
        })}
      </div>
      {isOpen && (
        <div class="popup-box">
          <div class="box">
            <form className="userForm">
              <div className="form-title">
                <h2>Edit the user</h2>
                <button className="close" onClick={togglePopup}>
                  <AiOutlineCloseSquare />
                </button>
              </div>
              <div className="form-text">
                <label for="fname">First Name</label>
                <input
                  type="text"
                  id="fname"
                  name="fname"
                  value={first_name}
                  onChange={(e) => setFirst_Name(e.target.value)}
                />
              </div>
              <div className="form-text">
                <label for="lname">Last Name</label>
                <input
                  type="text"
                  id="lname"
                  name="lname"
                  value={last_name}
                  onChange={(e) => setLast_Name(e.target.value)}
                />
              </div>
              <div className="form-text">
                <label for="email">Email</label>
                <input
                  type="text"
                  id="email"
                  name="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="form-text">
                <label for="avatar">Avatar URL</label>
                <input
                  type="text"
                  id="avatar"
                  name="avatar"
                  value={avatar}
                  onChange={(e) => setAvatar(e.target.value)}
                />
              </div>
              <button className="btn">Submit</button>
            </form>
          </div>
        </div>
      )}

      <div className="addUser">
        <form className="userForm" onSubmit={createUser}>
          <h1>Add User</h1>
          <div className="form-text">
            <label for="fname">First Name</label>
            <input
              type="text"
              id="fname"
              name="fname"
              value={first_name}
              onChange={(e) => setFirst_Name(e.target.value)}
            />
          </div>
          <div className="form-text">
            <label for="lname">Last Name</label>
            <input
              type="text"
              id="lname"
              name="lname"
              value={last_name}
              onChange={(e) => setLast_Name(e.target.value)}
            />
          </div>
          <div className="form-text">
            <label for="email">Email</label>
            <input
              type="text"
              id="email"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-text">
            <label for="avatar">Avatar URL</label>
            <input
              type="text"
              id="avatar"
              name="avatar"
              value={avatar}
              onChange={(e) => setAvatar(e.target.value)}
            />
          </div>
          <button className="btn">Add</button>
        </form>
      </div>
    </div>
  );
};

export default App;
